class SingletonMeta(type):
    """
    Define an Instance operation that lets clients access its unique
    instance.
    """

    def __init__(cls, name, bases, attrs, **kwargs):
        super().__init__(name, bases, attrs)
        cls._instance = None

    def __call__(cls, *args, **kwargs):
        if cls._instance is None:
            cls._instance = super().__call__(*args, **kwargs)
        return cls._instance


class Gui(metaclass=SingletonMeta):

    def __init__(self):
        self._state: GuiControlState = NORMAL
        self._alpha: float = 1.0
        self._style: list = RAYGUI_STYLE_DEFAULT_DARK

    def __getitem__(self, key: int):
        return self._style[key]

    def _button_logic(self, clicked: bool, point: Vector2, bounds: Rectangle) -> bool:
        # check button state
        if check_collision_point_rec(point, bounds):
            if is_mouse_button_down(MOUSE_LEFT_BUTTON):
                self._state = PRESSED
            elif is_mouse_button_released(MOUSE_LEFT_BUTTON):
                clicked = True
            else:
                self._state = FOCUSED
        return clicked

    def enable(self):
        """Enable GUI controls."""
        self._state = NORMAL

    def disable(self):
        """Disable GUI controls."""
        self._state = DISABLED
    
    def fade(self, alpha: float):
        """Set GUI controls transparency (ranges from 0.0 to 1.0)."""
        self._alpha = max(0.0, min(float(alpha), 1.0))

    @property
    def style(self) -> list:      # TODO: fix return type
        return self._style

    #region Container/Separator controls
    def window_box(self, bounds: Rectangle, text: str) -> bool:
        clicked: bool = False
        state: GuiControlState = self._state

        offset_x: int = 10
        text_width: int = measure_text(text, self[DEFAULT_TEXT_SIZE])
        # text_height: int = self[DEFAULT_TEXT_SIZE]

        if bounds.width < text_width + offset_x * 2 + 16:
            bounds.width = text_width + offset_x * 2 + 16
        statusbar: Rectangle = Rectangle(bounds.x, bounds.y, bounds.width, 24)
        if bounds.height < 50:
            bounds.height = 50

        # Update
        # ---------------------------------------
        if state is not DISABLED:
            mouse_point: Vector2 = get_mouse_position()

            # check button state
            clicked = self._button_logic(clicked, mouse_point, (statusbar.x + statusbar.width - 24, statusbar.y, 20, statusbar.height))

        # Draw
        # ---------------------------------------
        rec = (bounds.x + WINDOW_BORDER_WIDTH,
               bounds.y + WINDOW_BORDER_WIDTH,
               bounds.width - WINDOW_BORDER_WIDTH * 2,
               bounds.height - WINDOW_BORDER_WIDTH * 2)
        rec2 = (statusbar.x,
                statusbar.width - 27 + WINDOW_PADDING * 2,
                statusbar.y + 1 + WINDOW_PADDING,
                25 - WINDOW_PADDING * 3,
                statusbar.height - 2 - WINDOW_PADDING * 2)

        border_color, base_color, text_color = {
            NORMAL: ('default.border.color.normal', 'default.base.color.normal', 'default.text.color.normal'),
            FOCUSED: ('default.border.color.normal', 'default.base.color.focused', 'default.text.color.focused'),
            PRESSED: ('default.border.color.normal', 'default.base.color.pressed', 'default.text.color.pressed'),
            DISABLED: ('default.border.color.disabled', 'default.base.color.disabled', 'default.text.color.disabled'),
        }.get(self._state, ('default.border.color.normal', 'default.base.color.normal', 'default.text.color.normal'))

        draw_rectangle_lines_ex(bounds, WINDOW_BORDER_WIDTH, fade(get_color(self[border_color]), self._alpha))
        draw_rectangle_rec(rec, fade(get_color(self['default.background.color']), self._alpha))
        self.statusbar(statusbar, text, offset_x)
        draw_rectangle_rec(rec2, fade(get_color(self[base_color]), self._alpha))
        draw_text('x', statusbar.x + statusbar.width - 16, statusbar.y + statusbar.height / 2 - self['default.text.size'], self['default.text.size'], fade(get_color(self[text_color]), self._alpha))

        return clicked

    def group_box(self, bounds: Rectangle, text: str) -> None:
        """Group Box control with title name"""
        state: GuiControlState = self._state

        # Draw
        # ---------------------------------------
        if state is NORMAL:
            draw_rectangle(bounds.x, bounds.y, GROUPBOX_LINE_THICK, bounds.height, fade(get_color(self[DEFAULT_LINES_COLOR]), self._alpha))
            draw_rectangle(bounds.x, bounds.y + bounds.height - 1, bounds.width, GROUPBOX_LINE_THICK, fade(get_color(self[DEFAULT_LINES_COLOR]), self._alpha))
            draw_rectangle(bounds.x + bounds.width - 1, bounds.y, GROUPBOX_LINE_THICK, bounds.height, fade(get_color(self[DEFAULT_LINES_COLOR]), self._alpha))
            if text == '':
                draw_rectangle(bounds.x, bounds.y, bounds.width, GROUPBOX_LINE_THICK, fade(get_color(self[DEFAULT_LINES_COLOR]), self._alpha))
            else:
                draw_rectangle(bounds.x, bounds.y, GROUPBOX_TEXT_PADDING, GROUPBOX_LINE_THICK, fade(get_color(self[DEFAULT_LINES_COLOR]), self._alpha))
                draw_rectangle(bounds.x + 2 * GROUPBOX_TEXT_PADDING + measure_text(text, self[DEFAULT_TEXT_SIZE]), bounds.y, bounds.width - 2*GROUPBOX_TEXT_PADDING - measure_text(text, self[DEFAULT_TEXT_SIZE]), GROUPBOX_LINE_THICK, fade(get_color(self[DEFAULT_LINES_COLOR]), self._alpha))
                draw_text(text, bounds.x + GROUPBOX_TEXT_PADDING + 2 * GROUPBOX_PADDING, bounds.y - 2*GROUPBOX_PADDING - GROUPBOX_LINE_THICK, self[DEFAULT_TEXT_SIZE], fade(get_color(self[LABEL_TEXT_COLOR_NORMAL]), self._alpha))

        elif state is DISABLED:
            draw_rectangle(bounds.x, bounds.y, GROUPBOX_LINE_THICK, bounds.height, fade(get_color(self[DEFAULT_BORDER_COLOR_DISABLED]), self._alpha))
            draw_rectangle(bounds.x, bounds.y + bounds.height - 1, bounds.width, GROUPBOX_LINE_THICK, fade(get_color(self[DEFAULT_BORDER_COLOR_DISABLED]), self._alpha))
            draw_rectangle(bounds.x + bounds.width - 1, bounds.y, GROUPBOX_LINE_THICK, bounds.height, fade(get_color(self[DEFAULT_BORDER_COLOR_DISABLED]), self._alpha))
            if text == '':
                draw_rectangle(bounds.x, bounds.y, bounds.width, GROUPBOX_LINE_THICK, fade(get_color(self[DEFAULT_LINES_COLOR]), self._alpha))
            else:
                draw_rectangle(bounds.x, bounds.y, GROUPBOX_TEXT_PADDING, GROUPBOX_LINE_THICK, fade(get_color(self[DEFAULT_BORDER_COLOR_DISABLED]), self._alpha))
                draw_rectangle(bounds.x + 2 * GROUPBOX_TEXT_PADDING + measure_text(text, self[DEFAULT_TEXT_SIZE]), bounds.y, bounds.width - 2 * GROUPBOX_TEXT_PADDING - measure_text(text, self[DEFAULT_TEXT_SIZE]), GROUPBOX_LINE_THICK, fade(get_color(self[DEFAULT_BORDER_COLOR_DISABLED]), self._alpha))
                draw_text(text, bounds.x + GROUPBOX_TEXT_PADDING + 2 * GROUPBOX_PADDING, bounds.y - 2 * GROUPBOX_PADDING - GROUPBOX_LINE_THICK, self[DEFAULT_TEXT_SIZE], fade(get_color(self[LABEL_TEXT_COLOR_DISABLED]), self._alpha))

    def line(self, bounds: Rectangle, thick: int):
        state: GuiControlState = self._state
        line: Rectangle = Rectangle(bounds.x, bounds.y + bounds.height / 2 - thick / 2, bounds.width, thick)
        
        if state in (NORMAL, FOCUSED, PRESSED):
            draw_rectangle_lines_ex(line, thick, fade(get_color(self[DEFAULT_LINES_COLOR]), self._alpha))
        elif state is DISABLED:
            draw_rectangle_lines_ex(line, thick, fade(get_color(self[DEFAULT_BORDER_COLOR_DISABLED]), self._alpha))

